mapper.update:
UPDATE BOARD
SET B_TITLE = #{bTitle}, B_CONTENT = #{bContent}, B_HIDDEN = #{bHidden}, MODIFY_DATE = SYSDATE, CANO = #{cano}, CNO = #{cno}
WHERE BNO = #{bno}

UPDATE BOARD
SET STATUS = 'N'
WHERE BNO = #{bno}

mapper.select:
SELECT * FROM BOARD
WHERE STATUS = 'Y'
AND BNO = #{nid}
